import React, { useState, useEffect } from "react";
import { Table, Button } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../../components/Loader";
import Message from "../../components/Message";
import { createService, listServices } from "../../actions/serviceActions";
import { SERVICE_CREATE_RESET } from "../../reducers/serviceReducers";
export default function ServicesScreen() {
  const dispatch = useDispatch();
  const serviceList = useSelector((state) => state.serviceList);
  const { loading, success, services, pages, error } = serviceList;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const serviceRegister = useSelector((state) => state.serviceRegister);
  const {
    loading: loadingCreate,
    service: serviceCreated,
    error: errorCreate,
    success: successCreate,
  } = serviceRegister;
  const [actualPage, setActualPage] = useState(1);

  const createServiceHandler = () => {
    dispatch(createService);
  };
  useEffect(() => {
    dispatch({ type: SERVICE_CREATE_RESET });
    !userInfo && history.push("/");
  }, [dispatch, userInfo, actualPage]);

  return (
    <div className="p-5">
      <Button variant="primary" onClick="">
        Registrar servicio
      </Button>
      <h2 className="mt-3">Servicios</h2>

      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : services?.length == 0 ? (
        <Message variant="warning">No hay servicios actualmente</Message>
      ) : (
        <Table striped bordered hover responsive className="table-sm">
          <thead>
            <tr>
              <th>Nombre</th>
              <th>Descripci√≥n</th>
              <th>Precio</th>
            </tr>
          </thead>
          <tbody>
            {services?.map((service) => (
              <tr key={service._id}>
                <td>{service.name}</td>
                <td>{service.details}</td>
                <td>{service.price}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </div>
  );
}
