import { Row, Col, Image, Button, Form } from "react-bootstrap";
import { Link } from "react-router-dom";
import React, { useState, useEffect } from "react";
import FormContainer from "../../components/Form";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../../components/Loader";
import Message from "../../components/Message";

import { getServiceDetails, updateService } from "../../actions/serviceActions";
import { SERVICE_UPDATE_RESET } from "../../constants/serviceConstants";

export default function EditServiceScreen({ match, history }) {
  const serviceId = match.params.id;
  const [name, setName] = useState("");
  const [details, setDetails] = useState("");
  const [price, setPrice] = useState(0);

  const dispatch = useDispatch();
  const serviceDetails = useSelector((state) => state.serviceDetails);
  const {
    error: errorDetails,
    loading: loadingDetails,
    service,
  } = serviceDetails;

  const serviceUpdate = useSelector((state) => state.serviceUpdate);
  const {
    error: errorUpdate,
    loading: loadingUpdate,
    success: successUpdate,
  } = serviceUpdate;

  useEffect(() => {
    if (successUpdate) {
      dispatch({
        type: SERVICE_UPDATE_RESET,
      });
      history.push("/services");
    } else {
      if (!service || service._id !== Number(serviceId))
        dispatch(getServiceDetails(serviceId));
      else {
        setName(service.name);
        setDetails(service.details);
        setPrice(service.price);
      }
    }
  }, [service, serviceId, successUpdate, history]);

  return (
    <FormContainer>
      <Link to="/services">Regresar</Link>
      <h1>Editar servicio</h1>
      {loadingDetails ? (
        <Loader />
      ) : errorDetails ? (
        <Message variant="danger">{errorDetails}</Message>
      ) : (
        <Form onSubmit={submitHandler}>
          <Form.Group controlId="name">
            <Form.Label>Nombre</Form.Label>
            <Form.Control
              type="text"
              placeholder="Ingresa su nombre"
              value={name}
              onChange={(e) => setName(e.target.value)}
            ></Form.Control>
          </Form.Group>
        </Form>
      )}
    </FormContainer>
  );
}
