import { useEffect } from "react";
import { Modal, Table } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../../../../Loader";
import Message from "../../../../Message";
import { history } from "../../../../../actions/consultActions";
import { LinkContainer } from "react-router-bootstrap";
import { CONSULT_HISTORY_RESET } from "../../../../../constants/consultConstants";
export default function ModalPatient({
  patient,
  lgShow,
  loadingPatientDetails,
  errorPatientDetails,
  hide,
}) {
  const dispatch = useDispatch();
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const consultHistory = useSelector((state) => state.consultHistory);
  const {
    loading: loadingHistory,
    error: errorHistory,
    consults,
  } = consultHistory;
  return (
    <Modal
      size="lg"
      show={lgShow}
      onHide={() => {
        hide();
      }}
      aria-labelledby="example-modal-sizes-title-lg"
    >
      <Modal.Header closeButton>
        <Modal.Title id="example-modal-sizes-title-lg">
          Detalles del paciente
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {loadingPatientDetails ? (
          <Loader />
        ) : errorPatientDetails ? (
          <Message variant="danger">{errorPatientDetails}</Message>
        ) : (
          <>
            {patient ? (
              <>
                <h5>Nombre</h5>
                <p>{patient["name"]}</p>
                <h5>Apellidos</h5>
                <p>{patient["lastName"]}</p>
                <h5>Edad</h5>
                <p>{patient["age"]}</p>
                <h5>Correo</h5>
                <p>
                  <a href={`mailto:${patient["email"]}`}>{patient["email"]}</a>
                </p>
                <h5>Teléfono</h5>
                <p>{patient["phone"]}</p>
                <h5>Historial</h5>
                {loadingHistory ? (
                  <Loader />
                ) : errorHistory ? (
                  <Message variant="danger">{errorHistory}</Message>
                ) : (
                  <>
                    {showHistory}
                  </>
                )}
              </>
            ) : (
              ""
            )}
          </>
        )}
      </Modal.Body>
    </Modal>
  );
}
