import { Row, Col, Image, Button, Form, ButtonGroup } from "react-bootstrap";
import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import TodayConsults from "./components/todayConsults";
import RegisterConsult from "./components/registerConsult";
import RegisterPatient from "./components/registerPatient";
import Patients from "./components/patients";
import EditPatient from "./components/updatePatient";
export default function Dashboard({ history }) {
  const [dashboardContent, setDashboardContent] = useState("");
  const registerConsult = () => {
    setDashboardContent("registerConsult");
  };
  const todayConsults = () => {
    setDashboardContent("todayConsults");
  };
  const addService = () => {
    setDashboardContent("addService");
  };
  const editService = () => {
    setDashboardContent("editService");
  };
  const patients = () => {
    setDashboardContent("patients");
  };
  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;
  const dispatch = useDispatch();

  useEffect(() => {}, [history, userInfo]);
  return (
    <>
      <Row style={{ height: "auto" }}>
        <Col
          md={3}
          className="p-5"
          style={{ borderRight: "1px solid #ccc", height: "90vh" }}
        >
          <div
            style={{
              display: "flex",
              alignItems: "center",
              flexDirection: "column",
              justifyContent: "center",
              height: "100%",
            }}
          >
            <h2 style={{ width: "100%", textAlign: "left" }}>Buenos días</h2>
            <ButtonGroup vertical className="w-100">
              <Button
                onClick={todayConsults}
                variant={
                  dashboardContent == "todayConsults"
                    ? "primary"
                    : "outline-primary"
                }
                className="my-3 w-100"
              >
                <i className="fas fa-home"></i> Consultas de hoy
              </Button>
              <Button
                onClick={registerConsult}
                variant={
                  dashboardContent == "registerConsult"
                    ? "primary"
                    : "outline-primary"
                }
                className="my-3 w-100"
              >
                <i className="fas fa-check"></i> Registrar consulta
              </Button>
              <Button
                onClick={addService}
                variant={
                  dashboardContent == "addService"
                    ? "primary"
                    : "outline-primary"
                }
                className="my-3 w-100"
              >
                <i className="fas fa-plus-square"></i> Agregar servicio
              </Button>
              <Button
                onClick={editService}
                variant={
                  dashboardContent == "editService"
                    ? "primary"
                    : "outline-primary"
                }
                className="my-3 w-100"
              >
                <i className="fas fa-edit"></i> Editar servicio
              </Button>
              <Button
                onClick={editService}
                variant={
                  dashboardContent == "allConsults"
                    ? "primary"
                    : "outline-primary"
                }
                className="my-3 w-100"
              >
                <i className="fas fa-list-alt"></i> Todas las consultas
              </Button>
              <Button
                onClick={patients}
                variant={
                  dashboardContent == "patients" ? "primary" : "outline-primary"
                }
                className="my-3 w-100"
              >
                <i className="fas fa-list-alt"></i> Pacientes
              </Button>
            </ButtonGroup>
          </div>
        </Col>
        <Col md={9} className="p-5">
          <div>
            {dashboardContent == "todayConsults" && (
              <TodayConsults></TodayConsults>
            )}
            {dashboardContent == "registerConsult" && (
              <RegisterConsult></RegisterConsult>
            )}
            {dashboardContent == "patients" && (
              <Patients
                onChange={(value) => setDashboardContent(value)}
              ></Patients>
            )}
            {dashboardContent == "registerPatient" && (
              <RegisterPatient
                onChange={(value) => setDashboardContent(value)}
              ></RegisterPatient>
            )}
            {dashboardContent == "editPatient" && (
              <EditPatient
                onChange={(value) => setDashboardContent(value)}
              ></EditPatient>
            )}
          </div>
        </Col>
      </Row>
    </>
  );
}
