import React, { useState, useEffect } from "react";
import { Table, Button, InputGroup, Form } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../../../Loader";
import Message from "../../../Message";

import { LinkContainer } from "react-router-bootstrap";

import {
  listPatients,
  deletePatient,
} from "../../../../actions/patientActions";

export default function Patients(props) {
  const dispatch = useDispatch();

  const patientList = useSelector((state) => state.patientList);
  const { loading, error, patients } = patientList;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const patientDelete = useSelector((state) => state.patientDelete);
  const { success: successDelete } = patientDelete;

  // console.log(props);
  const deletePatientHandler = (id) => {
    if (window.confirm("Â¿Seguro que deseas eliminar a este paciente?"))
      dispatch(deletePatient(id));
  };

  useEffect(() => {
    dispatch(listPatients());
  }, [dispatch, userInfo, successDelete]);
  return (
    <>
      <Button
        onClick={(event) => props.onChange("registerPatient")}
        variant="primary"
      >
        Registrar paciente
      </Button>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <>
          <Form.co>

          </Form.co>
          <Table striped bordered hover responsive className="table-sm mt-3">
            <thead>
              <tr>
                <th>ID</th>
                <th>Nombre</th>
                <th>Apellidos</th>
                <th>Correo</th>
                <th>Edad</th>
                <th>Telefono</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              {patients?.map((patient) => (
                <tr key={patient._id}>
                  <td>{patient._id}</td>
                  <td>{patient.name}</td>
                  <td>{patient.lastName}</td>
                  <td>{patient.email}</td>
                  <td>{patient.age}</td>
                  <td>{patient.phone}</td>
                  <td>
                    <Button
                      variant="light"
                      className="btn-sm"
                      onClick={(event) =>
                        props.onChangePatientEditId("editPatient", patient._id)
                      }
                    >
                      <i className="fas fa-edit"></i>
                    </Button>

                    <Button
                      variant="danger"
                      className="btn-sm"
                      onClick={() => deletePatientHandler(patient._id)}
                    >
                      <i className="fas fa-trash"></i>
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </>
      )}
    </>
  );
}
