import React, { useState, useEffect } from "react";
import { Table, Button, InputGroup } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../../../Loader";
import Message from "../../../Message";
import { LinkContainer } from "react-router-bootstrap";
import { listConsults } from "../../../../actions/consultActions";
import moment from "moment";
import Datetime from "react-datetime";
import "react-datetime/css/react-datetime.css";

export default function Consults(props) {
  const dispatch = useDispatch();

  const consultList = useSelector((state) => state.consultList);
  const { loading, error, consults } = consultList;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const [dateSearch, setDateSearch] = useState();

  const handleChange = (date) => {
    if (date.startsWith("/[^a-zA-Z]+/")) console.log("");
    else set
  };
  useEffect(() => {
    dateSearch == undefined && dispatch(listConsults("allconsults"));
  }, [dispatch, userInfo]);

  const searchHandler = () => {
    dateSearch == undefined
      ? dispatch(listConsults("allconsults"))
      : dispatch(listConsults(dateSearch));
  };
  return (
    <>
      <h1>Consultas</h1>
      <InputGroup>
        <InputGroup.Append>
          <Datetime
            inputProps={{ placeholder: "Buscar una consulta", disabled: false }}
            value={dateSearch}
            dateFormat={"YYYY-MM-DD"}
            timeFormat={false}
            onChange={handleChange}
          />
          <InputGroup.Text
            id="datetime"
            style={{ marginTop: "0", cursor: "pointer" }}
            onClick={searchHandler}
          >
            Buscar
          </InputGroup.Text>
        </InputGroup.Append>
      </InputGroup>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Table striped bordered hover responsive className="table-sm mt-3">
          <thead>
            <tr>
              <th>ID</th>
              <th>Encargado</th>
              <th>Paciente</th>
              <th>Fecha</th>
              <th>Precio</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {consults?.map((consult) => (
              <tr key={consult._id}>
                <td>{consult._id}</td>
                {userInfo.isAdmin ? (
                  <LinkContainer
                    className="editPerson"
                    to={`/admin/user/${consult.user["_id"]}/edit`}
                  >
                    <td>{consult.user["name"]}</td>
                  </LinkContainer>
                ) : (
                  <td>{consult.user["name"]}</td>
                )}
                <td
                  className="editPerson"
                  onClick={() =>
                    props.onChangePatientEditId(
                      "editPatient",
                      consult.patient["_id"]
                    )
                  }
                >
                  {consult.patient["name"]}
                </td>
                <td>{consult.date.substring(0, 10)}</td>
                <td>$ {consult.price}</td>
                <td>
                  <Button variant="info" className="btn-sm">
                    <i className="fas fa-eye"></i>
                  </Button>
                  <Button variant="light" className="btn-sm">
                    <i className="fas fa-edit"></i>
                  </Button>

                  <Button variant="danger" className="btn-sm">
                    <i className="fas fa-trash"></i>
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
    </>
  );
}
