import { useEffect } from "react";
import { Modal } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../../../../Loader";
import Message from "../../../../Message";
import { history } from "../../../../../actions/consultActions";

export default function ModalPatient({
  patient,
  lgShow,
  loadingPatientDetails,
  errorPatientDetails,
  hide,
}) {
  const dispatch = useDispatch();

  const consultHistory = useSelector((state) => state.consultHistory);
  const {
    loading: loadingHistory,
    error: errorHistory,
    consults,
  } = consultHistory;
  useEffect(() => {
    if (patient._id != undefined) dispatch(history("patient", patient._id, 1));
  }, patient);
  return (
    <Modal
      size="lg"
      show={lgShow}
      onHide={() => hide()}
      aria-labelledby="example-modal-sizes-title-lg"
    >
      <Modal.Header closeButton>
        <Modal.Title id="example-modal-sizes-title-lg">
          Detalles del paciente
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {loadingPatientDetails ? (
          <Loader />
        ) : errorPatientDetails ? (
          <Message variant="danger">{errorPatientDetails}</Message>
        ) : (
          <>
            {patient ? (
              <>
                <h5>Nombre</h5>
                <p>{patient["name"]}</p>
                <h5>Apellidos</h5>
                <p>{patient["lastName"]}</p>
                <h5>Edad</h5>
                <p>{patient["age"]}</p>
                <h5>Correo</h5>
                <p>
                  <a href={`mailto:${patient["email"]}`}>{patient["email"]}</a>
                </p>
                <h5>Teléfono</h5>
                <p>{patient["phone"]}</p>
                <h5>Historial</h5>
                {loadingHistory ? (
                  <Loader />
                ) : errorHistory ? (
                  <Message variant="danger">{errorHistory}</Message>
                ) : (
                  <>
                          <Table striped bordered hover responsive className="table-sm mt-3">
          <thead>
            <tr>
              <th>ID</th>
              <th>Encargado</th>
              <th>Paciente</th>
              <th>Fecha</th>
              <th>Precio</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {consults?.map((consult) => (
              <tr key={consult._id}>
                <td>{consult._id}</td>
                {userInfo.isAdmin ? (
                  <LinkContainer
                    className="editPerson"
                    to={`/admin/user/${consult.user["_id"]}/edit`}
                  >
                    <td>{consult.user["name"]}</td>
                  </LinkContainer>
                ) : (
                  <td>{consult.user["name"]}</td>
                )}
                <td
                  className="editPerson"
                  onClick={() =>
                    props.onChangePatientEditId(
                      "editPatient",
                      consult.patient["_id"]
                    )
                  }
                >
                  {consult.patient["name"]}
                </td>
                <td>{consult.date.substring(0, 10)}</td>
                <td>$ {consult.price}</td>
                <td>
                  <Button
                    variant="info"
                    className="btn-sm"
                    onClick={() => {
                      getConsultDetailsHandler(consult._id);
                      setLgShow(true);
                    }}
                  >
                    <i className="fas fa-eye"></i>
                  </Button>
                  <Button
                    variant="light"
                    className="btn-sm"
                    onClick={() => {
                      props.onChangeConsultEditId("editConsult", consult._id);
                    }}
                  >
                    <i className="fas fa-edit"></i>
                  </Button>

                  <Button
                    onClick={() => deleteConsultHandler(consult._id)}
                    variant="danger"
                    className="btn-sm"
                  >
                    <i className="fas fa-trash"></i>
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table></>
                )}
              </>
            ) : (
              ""
            )}
          </>
        )}
      </Modal.Body>
    </Modal>
  );
}
