import React, { useState, useEffect } from "react";
import { Table, Button, InputGroup, Modal } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../../../Loader";
import Message from "../../../Message";
import { LinkContainer } from "react-router-bootstrap";
import {
  listConsults,
  consultDetails,
  consultDelete,
} from "../../../../actions/consultActions";
import moment from "moment";
import Datetime from "react-datetime";
import "react-datetime/css/react-datetime.css";
import ModalHeader from "react-bootstrap/esm/ModalHeader";

export default function TodayConsults(props) {
  moment.locale("es-mx");
  const dispatch = useDispatch();

  const [lgShow, setLgShow] = useState(false);

  const consultList = useSelector((state) => state.consultList);
  const { loading, error, consults } = consultList;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  var todayDate = new moment().format("YYYY-MM-DD");
  const [dateSearch, setDateSearch] = useState(todayDate);

  const consultDetailsReducer = useSelector((state) => state.consultDetails);
  const {
    loading: loadingDetails,
    error: errorDetails,
    consult: myConsult,
  } = consultDetailsReducer;

  const consultDeleteReducer = useSelector((state) => state.consultDelete);
  const {
    loading: loadingDelete,
    error: errorDelete,
    success: successDelete,
  } = consultDeleteReducer;

  useEffect(() => {
    dispatch(listConsults(dateSearch));
  }, [dispatch, userInfo, successDelete]);

  const getConsultDetails = (id) => {
    dispatch(consultDetails(id));
  };

  const deleteConsultHandler = (id) => {
    window.confirm("Â¿Seguro que deseas eliminar a este paciente?") &&
      dispatch(consultDelete(id));
  };

  const [lgEditShow, setEditLgShow] = useState(false);


  return (
    <>
      <h1>Consultas de hoy</h1>

      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Table striped bordered hover responsive className="table-sm mt-3">
          <thead>
            <tr>
              <th>ID</th>
              <th>Encargado</th>
              <th>Paciente</th>

              <th>Precio</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {consults?.map((consult) => (
              <tr key={consult._id}>
                <td>{consult._id}</td>
                {userInfo.isAdmin ? (
                  <LinkContainer
                    className="editPerson"
                    to={`/admin/user/${consult.user["_id"]}/edit`}
                  >
                    <td>{consult.user["name"]}</td>
                  </LinkContainer>
                ) : (
                  <td>{consult.user["name"]}</td>
                )}
                <td
                  className="editPerson"
                  onClick={() =>
                    props.onChangePatientEditId(
                      "editPatient",
                      consult.patient["_id"]
                    )
                  }
                >
                  {consult.patient["name"]}
                </td>
                <td>$ {consult.price}</td>
                <td>
                  <Button
                    variant="info"
                    className="btn-sm"
                    onClick={() => {
                      getConsultDetails(consult._id);
                      setLgShow(true);
                    }}
                  >
                    <i className="fas fa-eye"></i>
                  </Button>
                  <Button variant="light" className="btn-sm">
                    <i className="fas fa-edit"></i>
                  </Button>

                  <Button
                    variant="danger"
                    className="btn-sm"
                    onClick={() => deleteConsultHandler(consult._id)}
                  >
                    <i className="fas fa-trash"></i>
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}

      <Modal
        size="lg"
        show={lgShow}
        onHide={() => setLgShow(false)}
        aria-labelledby="example-modal-sizes-title-lg"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-modal-sizes-title-lg">
            Detalles de la consulta
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {loadingDetails ? (
            <Loader />
          ) : errorDetails ? (
            <Message variant="danger">{errorDetails}</Message>
          ) : (
            <>
              {myConsult ? (
                <>
                  <h5>Paciente</h5>
                  <p
                    className="editPerson"
                    onClick={() =>
                      props.onChangePatientEditId(
                        "editPatient",
                        myConsult.patient["_id"]
                      )
                    }
                  >
                    {myConsult.patient["name"]}
                  </p>
                  <h5>Encargado</h5>
                  <LinkContainer
                    className="editPerson"
                    to={`/admin/user/${myConsult.user["_id"]}/edit`}
                  >
                    <p>{myConsult.user["name"]}</p>
                  </LinkContainer>
                  <h5>Detalles</h5>
                  <p>{myConsult.details}</p>
                  <h5>Abono</h5>
                  <p>$ {myConsult.price}</p>
                  <h5>Hora</h5>
                  <p className="text-capitalize">
                    {moment(myConsult.date).format("h:mm A")}
                  </p>
                </>
              ) : (
                ""
              )}
            </>
          )}
        </Modal.Body>
      </Modal>
      <Modal
        size="lg"
        show={lgEditShow}
        onHide={() => setLgEditShow(false)}
        aria-labelledby="example-modal-sizes-title-lg"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-modal-sizes-title-lg">
            Detalles de la consulta
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {loadingDetails ? (
            <Loader />
          ) : errorDetails ? (
            <Message variant="danger">{errorDetails}</Message>
          ) : (
            <>
              {myConsult ? (
                <>
                  <h5>Paciente</h5>
                  <p
                    className="editPerson"
                    onClick={() =>
                      props.onChangePatientEditId(
                        "editPatient",
                        myConsult.patient["_id"]
                      )
                    }
                  >
                    {myConsult.patient["name"]}
                  </p>
                  <h5>Encargado</h5>
                  <LinkContainer
                    className="editPerson"
                    to={`/admin/user/${myConsult.user["_id"]}/edit`}
                  >
                    <p>{myConsult.user["name"]}</p>
                  </LinkContainer>
                  <h5>Detalles</h5>
                  <p>{myConsult.details}</p>
                  <h5>Abono</h5>
                  <p>$ {myConsult.price}</p>
                  <h5>Hora</h5>
                  <p className="text-capitalize">
                    {moment(myConsult.date).format("h:mm A")}
                  </p>
                </>
              ) : (
                ""
              )}
            </>
          )}
        </Modal.Body>
      </Modal>
    </>
  );
}
