import { Row, Col, Image, Button, Form, ButtonGroup } from "react-bootstrap";
import React, { useState, useEffect } from "react";

import TodayConsults from "./components/todayConsults";
export default function Dashboard() {
  const [dashboardContent, setDashboardContent] = useState("");
  const registerConsult = () => {
    setDashboardContent("registerConsult");
  };
  const todayConsults = () => {
    setDashboardContent("todayConsults");
  };
  const addService = () => {
    setDashboardContent("addService");
  };
  const editService = () => {
    setDashboardContent("editService");
  };
  return (
    <>
      <Row style={{ height: "90vh" }}>
        <Col md={3} className="p-5" style={{ borderRight: "1px solid #ccc" }}>
          <div
            style={{
              display: "flex",
              alignItems: "center",
              flexDirection: "column",
              justifyContent: "center",
              height: "100%",
            }}
          >
            <h2 style={{ width: "100%", textAlign: "left" }}>Buenos d√≠as</h2>
            <ButtonGroup vertical className="w-100">
              <Button
                onClick={todayConsults}
                variant={
                  dashboardContent == "todayConsults"
                    ? "primary"
                    : "outline-primary"
                }
                className="my-3 w-100"
              >
                <i className="fas fa-home"></i> Consultas de hoy
              </Button>
              <Button
                onClick={registerConsult}
                variant={
                  dashboardContent == "todayConsults"
                    ? "primary"
                    : "outline-primary"
                }
                className="my-3 w-100"
              >
                <i className="fas fa-check"></i> Registrar consulta
              </Button>
              <Button
                onClick={addService}
                variant={
                  dashboardContent == "todayConsults"
                    ? "primary"
                    : "outline-primary"
                }
                className="my-3 w-100"
              >
                <i className="fas fa-home"></i> Agregar servicio
              </Button>
              <Button
                onClick={editService}
                variant={
                  dashboardContent == "todayConsults"
                    ? "primary"
                    : "outline-primary"
                }
                className="my-3 w-100"
              >
                <i className="fas fa-edit"></i> Editar servicio
              </Button>
              <Button
                onClick={editService}
                variant={
                  dashboardContent == "todayConsults"
                    ? "primary"
                    : "outline-primary"
                }
                className="my-3 w-100"
              >
                <i className="fas fa-edit"></i> Todas las consultas
              </Button>
            </ButtonGroup>
          </div>
        </Col>
        <Col md={9}>
          <div>
            {dashboardContent == "todayConsults" && (
              <TodayConsults></TodayConsults>
            )}
          </div>
        </Col>
      </Row>
    </>
  );
}
