import React, { useState, useEffect } from "react";
import { Table, Button, InputGroup, Modal } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import Loader from "../../../Loader";
import Message from "../../../Message";
import { LinkContainer } from "react-router-bootstrap";
import {
  listConsults,
  consultDetails,
} from "../../../../actions/consultActions";
import moment from "moment";
import Datetime from "react-datetime";
import "react-datetime/css/react-datetime.css";
import "moment/locale/es-mx";

export default function Consults(props) {
  moment.locale("es-mx");
  const dispatch = useDispatch();

  const [lgShow, setLgShow] = useState(false);

  const consultList = useSelector((state) => state.consultList);
  const { loading, error, consults } = consultList;

  const userLogin = useSelector((state) => state.userLogin);
  const { userInfo } = userLogin;

  const [dateSearch, setDateSearch] = useState();

  const consultDetailsReducer = useSelector((state) => state.consultDetails);
  const {
    loading: loadingDetails,
    error: errorDetails,
    consult: myConsult,
  } = consultDetailsReducer;

  const handleChange = (date) => {
    typeof date == "string"
      ? console.log("")
      : setDateSearch(date.format("YYYY-MM-DD"));
  };
  useEffect(() => {
    dateSearch == undefined && dispatch(listConsults("allconsults"));
  }, [dispatch, userInfo]);

  const searchHandler = () => {
    dateSearch == undefined
      ? dispatch(listConsults("allconsults"))
      : dispatch(listConsults(dateSearch));
  };

  const getConsultDetails = (id) => {
    dispatch(consultDetails(id));
  };
  return (
    <>
      <h1>Consultas</h1>
      <InputGroup>
        <InputGroup.Append>
          <Datetime
            inputProps={{ placeholder: "Buscar una consulta" }}
            value={dateSearch}
            dateFormat={"YYYY-MM-DD"}
            timeFormat={false}
            onChange={handleChange}
          />
          <InputGroup.Text
            id="datetime"
            style={{ marginTop: "0", cursor: "pointer" }}
            onClick={searchHandler}
          >
            Buscar
          </InputGroup.Text>
        </InputGroup.Append>
      </InputGroup>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : consults.length == 0 ? (
        <Message variant="warning">No hay citas en esta fecha</Message>
      ) : (
        <Table striped bordered hover responsive className="table-sm mt-3">
          <thead>
            <tr>
              <th>ID</th>
              <th>Encargado</th>
              <th>Paciente</th>
              <th>Fecha</th>
              <th>Precio</th>
              <th></th>
            </tr>
          </thead>
          <tbody>
            {consults?.map((consult) => (
              <tr key={consult._id}>
                <td>{consult._id}</td>
                {userInfo.isAdmin ? (
                  <LinkContainer
                    className="editPerson"
                    to={`/admin/user/${consult.user["_id"]}/edit`}
                  >
                    <td>{consult.user["name"]}</td>
                  </LinkContainer>
                ) : (
                  <td>{consult.user["name"]}</td>
                )}
                <td
                  className="editPerson"
                  onClick={() =>
                    props.onChangePatientEditId(
                      "editPatient",
                      consult.patient["_id"]
                    )
                  }
                >
                  {consult.patient["name"]}
                </td>
                <td>{consult.date.substring(0, 10)}</td>
                <td>$ {consult.price}</td>
                <td>
                  <Button
                    variant="info"
                    className="btn-sm"
                    onClick={() => {
                      getConsultDetails(consult._id);
                      setLgShow(true);
                    }}
                  >
                    <i className="fas fa-eye"></i>
                  </Button>
                  <Button variant="light" className="btn-sm">
                    <i className="fas fa-edit"></i>
                  </Button>

                  <Button variant="danger" className="btn-sm">
                    <i className="fas fa-trash"></i>
                  </Button>
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      )}
      <Modal
        size="lg"
        show={lgShow}
        onHide={() => setLgShow(false)}
        aria-labelledby="example-modal-sizes-title-lg"
      >
        <Modal.Header closeButton>
          <Modal.Title id="example-modal-sizes-title-lg">
            Detalles de la consulta
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {loadingDetails ? (
            <Loader />
          ) : errorDetails ? (
            <Message variant="danger">{errorDetails}</Message>
          ) : (
            <>
              {myConsult ? (
                <>
                  <h5>Paciente</h5>
                  <p
                    className="editPerson"
                    onClick={() =>
                      props.onChangePatientEditId(
                        "editPatient",
                        myConsult.patient["_id"]
                      )
                    }
                  >
                    {myConsult.patient["name"]}
                  </p>
                  <h5>Encargado</h5>
                  <LinkContainer
                    className="editPerson"
                    to={`/admin/user/${myConsult.user["_id"]}/edit`}
                  >
                    <p>{myConsult.user["name"]}</p>
                  </LinkContainer>
                  <h5>Detalles</h5>
                  <p>{myConsult.details}</p>
                  <h5>Abono</h5>
                  <p>$ {myConsult.price}</p>
                  <h5>Fecha</h5>
                  <p>
                    {moment(myConsult.date).format("MMMM Do YYYY, h:mm:ss a").toUpperCase()}
                  </p>
                </>
              ) : (
                ""
              )}
            </>
          )}
        </Modal.Body>
      </Modal>
    </>
  );
}
